/**
 * Sales order details block
 *
 * @category   Mage
 * @package    Mage_Sales
 * @author      Magento Core Team <core@magentocommerce.com>
 */

class SM_Sales_Block_Order_Print_Invoices extends Mage_Sales_Block_Items_Abstract
{
    protected function _prepareLayout()
    {
    }

    public function getBackUrl()
    {
        return Mage::getUrl('*/*/history');
    }

    public function getPrintUrl()
    {
        return Mage::getUrl('*/*/print');
    }

    public function getPaymentInfoHtml($order)
    {
        return $this->helper('payment')->getInfoBlock($order->getPayment());
    }

    public function getOrders()
    {
        return Mage::registry('current_orders');
    }

    protected function _prepareItem(Mage_Core_Block_Abstract $renderer)
    {
        $renderer->setPrintStatus(true);
        return parent::_prepareItem($renderer);
    }

    /**
     * Get html of invoice totlas block
     *
     * @param   Mage_Sales_Model_Order_Invoice $invoice
     * @return  string
     */
    public function getInvoiceTotalsHtml($invoice)
    {
        $html = '';
        $totals = $this->getChild('invoice_totals');

        if ($totals) {
            $totals->setInvoice($invoice);
            $totals->setOrder($invoice->getOrder());

            $html = $totals->toHtml();
        }

        return $html;
    }
}

